enifed('ember-metal/tests/events_test', ['ember-metal/mixin', 'ember-metal/platform/create', 'ember-metal/utils', 'ember-metal/events'], function (mixin, create, utils, events) {

  'use strict';

  QUnit.module('system/props/events_test');

  QUnit.test('listener should receive event - removing should remove', function() {
    var obj = {};
    var count = 0;
    var F = function() { count++; };

    events.addListener(obj, 'event!', F);
    equal(count, 0, 'nothing yet');

    events.sendEvent(obj, 'event!');
    equal(count, 1, 'received event');

    events.removeListener(obj, 'event!', F);

    count = 0;
    events.sendEvent(obj, 'event!');
    equal(count, 0, 'received event');
  });

  QUnit.test('listeners should be inherited', function() {
    var obj = {};
    var count = 0;
    var F = function() { count++; };

    events.addListener(obj, 'event!', F);

    var obj2 = create['default'](obj);

    equal(count, 0, 'nothing yet');

    events.sendEvent(obj2, 'event!');
    equal(count, 1, 'received event');

    events.removeListener(obj2, 'event!', F);

    count = 0;
    events.sendEvent(obj2, 'event!');
    equal(count, 0, 'did not receive event');

    events.sendEvent(obj, 'event!');
    equal(count, 1, 'should still invoke on parent');

  });


  QUnit.test('adding a listener more than once should only invoke once', function() {

    var obj = {};
    var count = 0;
    var F = function() { count++; };
    events.addListener(obj, 'event!', F);
    events.addListener(obj, 'event!', F);

    events.sendEvent(obj, 'event!');
    equal(count, 1, 'should only invoke once');
  });

  QUnit.test('adding a listener with a target should invoke with target', function() {
    var obj = {};
    var target;

    target = {
      count: 0,
      method: function() { this.count++; }
    };

    events.addListener(obj, 'event!', target, target.method);
    events.sendEvent(obj, 'event!');
    equal(target.count, 1, 'should invoke');
  });

  QUnit.test('suspending a listener should not invoke during callback', function() {
    var obj = {};
    var target, otherTarget;

    target = {
      count: 0,
      method: function() { this.count++; }
    };

    otherTarget = {
      count: 0,
      method: function() { this.count++; }
    };

    events.addListener(obj, 'event!', target, target.method);
    events.addListener(obj, 'event!', otherTarget, otherTarget.method);

    function callback() {
      /*jshint validthis:true */
      equal(this, target);

      events.sendEvent(obj, 'event!');

      return 'result';
    }

    events.sendEvent(obj, 'event!');

    equal(events.suspendListener(obj, 'event!', target, target.method, callback), 'result');

    events.sendEvent(obj, 'event!');

    equal(target.count, 2, 'should invoke');
    equal(otherTarget.count, 3, 'should invoke');
  });

  QUnit.test('adding a listener with string method should lookup method on event delivery', function() {
    var obj = {};
    var target;

    target = {
      count: 0,
      method: function() {}
    };

    events.addListener(obj, 'event!', target, 'method');
    events.sendEvent(obj, 'event!');
    equal(target.count, 0, 'should invoke but do nothing');

    target.method = function() { this.count++; };
    events.sendEvent(obj, 'event!');
    equal(target.count, 1, 'should invoke now');
  });

  QUnit.test('calling sendEvent with extra params should be passed to listeners', function() {

    var obj = {};
    var params = null;
    events.addListener(obj, 'event!', function() {
      params = Array.prototype.slice.call(arguments);
    });

    events.sendEvent(obj, 'event!', ['foo', 'bar']);
    deepEqual(params, ['foo', 'bar'], 'params should be saved');
  });

  QUnit.test('implementing sendEvent on object should invoke', function() {
    var obj = {
      sendEvent: function(eventName, params) {
        equal(eventName, 'event!', 'eventName');
        deepEqual(params, ['foo', 'bar']);
        this.count++;
      },

      count: 0
    };

    events.addListener(obj, 'event!', obj, function() { this.count++; });

    events.sendEvent(obj, 'event!', ['foo', 'bar']);
    equal(obj.count, 2, 'should have invoked method & listener');
  });

  QUnit.test('hasListeners tells you if there are listeners for a given event', function() {

    var obj = {};
    var F = function() {};
    var F2 = function() {};

    equal(events.hasListeners(obj, 'event!'), false, 'no listeners at first');

    events.addListener(obj, 'event!', F);
    events.addListener(obj, 'event!', F2);

    equal(events.hasListeners(obj, 'event!'), true, 'has listeners');

    events.removeListener(obj, 'event!', F);
    equal(events.hasListeners(obj, 'event!'), true, 'has listeners');

    events.removeListener(obj, 'event!', F2);
    equal(events.hasListeners(obj, 'event!'), false, 'has no more listeners');

    events.addListener(obj, 'event!', F);
    equal(events.hasListeners(obj, 'event!'), true, 'has listeners');
  });

  QUnit.test('calling removeListener without method should remove all listeners', function() {
    var obj = {};
    var F = function() {};
    var F2 = function() {};

    equal(events.hasListeners(obj, 'event!'), false, 'no listeners at first');

    events.addListener(obj, 'event!', F);
    events.addListener(obj, 'event!', F2);

    equal(events.hasListeners(obj, 'event!'), true, 'has listeners');

    events.removeListener(obj, 'event!');

    equal(events.hasListeners(obj, 'event!'), false, 'has no more listeners');
  });

  QUnit.test('while suspended, it should not be possible to add a duplicate listener', function() {
    var obj = {};
    var target;

    target = {
      count: 0,
      method: function() { this.count++; }
    };

    events.addListener(obj, 'event!', target, target.method);

    function callback() {
      events.addListener(obj, 'event!', target, target.method);
    }

    events.sendEvent(obj, 'event!');

    events.suspendListener(obj, 'event!', target, target.method, callback);

    equal(target.count, 1, 'should invoke');
    equal(utils.meta(obj).listeners['event!'].length, 3, "a duplicate listener wasn't added");

    // now test suspendListeners...

    events.sendEvent(obj, 'event!');

    events.suspendListeners(obj, ['event!'], target, target.method, callback);

    equal(target.count, 2, 'should have invoked again');
    equal(utils.meta(obj).listeners['event!'].length, 3, "a duplicate listener wasn't added");
  });

  QUnit.test('a listener can be added as part of a mixin', function() {
    var triggered = 0;
    var MyMixin = mixin.Mixin.create({
      foo1: events.on('bar', function() {
        triggered++;
      }),

      foo2: events.on('bar', function() {
        triggered++;
      })
    });

    var obj = {};
    MyMixin.apply(obj);

    events.sendEvent(obj, 'bar');
    equal(triggered, 2, 'should invoke listeners');
  });

  QUnit.test('a listener added as part of a mixin may be overridden', function() {

    var triggered = 0;
    var FirstMixin = mixin.Mixin.create({
      foo: events.on('bar', function() {
        triggered++;
      })
    });
    var SecondMixin = mixin.Mixin.create({
      foo: events.on('baz', function() {
        triggered++;
      })
    });

    var obj = {};
    FirstMixin.apply(obj);
    SecondMixin.apply(obj);

    events.sendEvent(obj, 'bar');
    equal(triggered, 0, 'should not invoke from overriden property');

    events.sendEvent(obj, 'baz');
    equal(triggered, 1, 'should invoke from subclass property');
  });

});
