    Beer.prototype.type = 'ipa';

    var beer = new Beer();

    observer.addObserver(beer, 'brand', K);

    deepEqual(keys['default'](beer), []);

    property_set.set(beer, 'brand', 'Corona');
    deepEqual(keys['default'](beer), ['brand']);

    observer.removeObserver(beer, 'brand', K);
