enifed('ember-metal/tests/mixin/computed_test', ['ember-metal/property_get', 'ember-metal/property_set', 'ember-metal/mixin', 'ember-metal/computed', 'ember-metal/properties'], function (property_get, property_set, mixin, computed, properties) {

  'use strict';

  function K() { return this; }

  QUnit.module('Mixin Computed Properties');

  QUnit.test('overriding computed properties', function() {
    var MixinA, MixinB, MixinC, MixinD;
    var obj;

    MixinA = mixin.Mixin.create({
      aProp: computed.computed(function() {
        return 'A';
      })
    });

    MixinB = mixin.Mixin.create(MixinA, {
      aProp: computed.computed(function() {
        return this._super.apply(this, arguments)+'B';
      })
    });

    MixinC = mixin.Mixin.create(MixinA, {
      aProp: computed.computed(function() {
        return this._super.apply(this, arguments)+'C';
      })
    });

    MixinD = mixin.Mixin.create({
      aProp: computed.computed(function() {
        return this._super.apply(this, arguments)+'D';
      })
    });

    obj = {};
    MixinB.apply(obj);
    equal(property_get.get(obj, 'aProp'), 'AB', "should expose super for B");

    obj = {};
    MixinC.apply(obj);
    equal(property_get.get(obj, 'aProp'), 'AC', "should expose super for C");

    obj = {};

    MixinA.apply(obj);
    MixinD.apply(obj);
    equal(property_get.get(obj, 'aProp'), 'AD', "should define super for D");

    obj = { };
    properties.defineProperty(obj, 'aProp', computed.computed(function(key, value) {
      return 'obj';
    }));
    MixinD.apply(obj);
    equal(property_get.get(obj, 'aProp'), "objD", "should preserve original computed property");
  });

  QUnit.test('calling set on overridden computed properties', function() {
    var SuperMixin, SubMixin;
    var obj;

    var superGetOccurred = false;
    var superSetOccurred = false;

    SuperMixin = mixin.Mixin.create({
      aProp: computed.computed(function(key, val) {
        if (arguments.length === 1) {
          superGetOccurred = true;
        } else {
          superSetOccurred = true;
        }
        return true;
      })
    });

    SubMixin = mixin.Mixin.create(SuperMixin, {
      aProp: computed.computed(function(key, val) {
        return this._super.apply(this, arguments);
      })
    });

    obj = {};
    SubMixin.apply(obj);

    property_set.set(obj, 'aProp', 'set thyself');
    ok(superSetOccurred, 'should pass set to _super');

    superSetOccurred = false; // reset the set assertion

    obj = {};
    SubMixin.apply(obj);

    property_get.get(obj, 'aProp');
    ok(superGetOccurred, 'should pass get to _super');

    property_set.set(obj, 'aProp', 'set thyself');
    ok(superSetOccurred, 'should pass set to _super after getting');
  });

  QUnit.test('setter behavior works properly when overriding computed properties', function() {
    var obj = {};

    var MixinA = mixin.Mixin.create({
      cpWithSetter2: computed.computed(K),
      cpWithSetter3: computed.computed(K),
      cpWithoutSetter: computed.computed(K)
    });

    var cpWasCalled = false;

    var MixinB = mixin.Mixin.create({
      cpWithSetter2: computed.computed(function(k, v) {
        cpWasCalled = true;
      }),

      cpWithSetter3: computed.computed(function(k, v) {
        cpWasCalled = true;
      }),

      cpWithoutSetter: computed.computed(function(k) {
        cpWasCalled = true;
      })
    });

    MixinA.apply(obj);
    MixinB.apply(obj);

    property_set.set(obj, 'cpWithSetter2', 'test');
    ok(cpWasCalled, "The computed property setter was called when defined with two args");
    cpWasCalled = false;

    property_set.set(obj, 'cpWithSetter3', 'test');
    ok(cpWasCalled, "The computed property setter was called when defined with three args");
    cpWasCalled = false;

    property_set.set(obj, 'cpWithoutSetter', 'test');
    equal(property_get.get(obj, 'cpWithoutSetter'), 'test', "The default setter was called, the value is correct");
    ok(!cpWasCalled, "The default setter was called, not the CP itself");
  });

});
