    properties.defineProperty(foo.bar.baz, 'bam', computed.computed(function() {
      count++;
    }));

    observer.addObserver(foo, 'bar.baz.bam', function() {});

    property_events.propertyDidChange(property_get.get(foo, 'bar.baz'), 'bam');

